Vue.component("sitemap-item",{props:["item"],template:"<li class='dd-item' :data-id='item.id'>  <div class='sitemap-item'>    <div class='handle dd-handle'><i class='fas fa-ellipsis-v'></i></div>    <div class='link'>      <a :href='piranha.baseUrl + item.editUrl + item.id'>        {{ item.title }}        <span v-if='item.status' class='badge badge-primary'>{{ item.status }}</span>      </a>    </div>    <div class='type d-none d-md-block'>{{ item.typeName }}</div>    <div class='date d-none d-md-block'>{{ item.published }}</div>    <div class='actions'>      <a href='#' data-toggle='modal' data-target='#pageAddModal'><i class='fas fa-angle-down'></i></a>      <a href='#' data-toggle='modal' data-target='#pageAddModal'><i class='fas fa-angle-right'></i></a>      <a v-if='item.items.length === 0' v-on:click.prevent='piranha.pagelist.remove(item.id)' class='danger' href='#'><i class='fas fa-trash'></i></a>    </div>  </div>  <ol v-if='item.items.length > 0' class='dd-list'>    <sitemap-item v-for='child in item.items' v-bind:key='child.id' v-bind:item='child'>    </sitemap-item>  </ol></li>"}),piranha.pagelist=new Vue({el:"#pagelist",data:{loading:!0,updateBindings:!1,items:[],sites:[],pageTypes:[]},methods:{load:function(){fetch(piranha.baseUrl+"manager/api/page/list").then(function(a){return a.json()}).then(function(a){piranha.pagelist.sites=a.sites,piranha.pagelist.pageTypes=a.pageTypes,piranha.pagelist.updateBindings=!0}).catch(function(a){console.log("error:",a)})},remove:function(a){var i=this;fetch(piranha.baseUrl+"manager/api/page/delete/"+a).then(function(a){return a.json()}).then(function(a){piranha.notifications.push(a),i.load()}).catch(function(a){console.log("error:",a)})}},created:function(){},updated:function(){this.updateBindings&&($(".sitemap-container").nestable({maxDepth:100,group:1,callback:function(a,i){fetch(piranha.baseUrl+"manager/api/page/move",{method:"post",headers:{"Content-Type":"application/json"},body:JSON.stringify({id:$(i).attr("data-id"),items:$(a).nestable("serialize")})}).then(function(a){return a.json()}).then(function(a){piranha.notifications.push(a)}).catch(function(a){console.log("error:",a)})}}),this.updateBindings=!1),this.loading=!1}});